    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
        
        String conra="usuario";
        
       if(jComboBox1.getSelectedItem() == "Estudiante"){
            
          if(jTextField1.getText().equals("estudiante")){
           
            Estudiante ay =new Estudiante();
             ay.setVisible(true); 
             
             dispose();
           }else{
             
          JOptionPane.showMessageDialog(null, "Contraseña Incorrecta", "Validando Datos", 
          JOptionPane.ERROR_MESSAGE);  
               
           }
            
        }else{
             if(jComboBox1.getSelectedItem() == "Administrador"){
            
                 if(jTextField1.getText().equals("administrador")){
                     
                 
            Administrador ay =new Administrador();
             ay.setVisible(true); 
             
             dispose();
             
                 }else{
                   JOptionPane.showMessageDialog(null, "Contraseña Incorrecta", "Validando Datos", 
                   JOptionPane.ERROR_MESSAGE);   
                 }
            
        }else{
             
         JOptionPane.showMessageDialog(null, "Por Favor Seleccione Un Usuario", "Validando Datos", 
        JOptionPane.ERROR_MESSAGE);
        }
        }
    }//GEN-LAST:event_jButton2ActionPerformed

    
    
